(43, 12, 'directions_0_direction', 'Preheat oven to 350.'),
(44, 12, '_directions_0_direction', 'field_5aec26cd305c8'),
(45, 12, 'directions_1_direction', 'Heat olive oil in skillet, saute onions until translucent.'),
(46, 12, '_directions_1_direction', 'field_5aec26cd305c8'),
(47, 12, 'directions_2_direction', 'Add Beyond Meat Crumbles and taco seasoning. Saute until cooked.'),
(48, 12, '_directions_2_direction', 'field_5aec26cd305c8'),
(49, 12, 'directions_3_direction', 'Add beans (drained) and green chiles.  Mix well.'),
(50, 12, '_directions_3_direction', 'field_5aec26cd305c8'),
(51, 12, 'directions_4_direction', 'Add about half the can of green enchilada sauce. If you like really saucey, feel free to use multiple cans. Stir and heat through.'),
(52, 12, '_directions_4_direction', 'field_5aec26cd305c8'),
(53, 12, 'directions_5_direction', 'In a 13 x 9 or 14 x 9 casserole dish, spread a thin layer of green enchilada sauce on the bottom of pan.'),
(54, 12, '_directions_5_direction', 'field_5aec26cd305c8'),
(55, 12, 'directions_6_direction', 'Take 1 tortilla, sprinkle a line of cheese down the middle in a straight line, scoop about 1/4 - 1/2 cup of the meat/bean/sauce mixture down the middle of tortilla. Wrap tightly and place seam side down in dish.'),
(56, 12, '_directions_6_direction', 'field_5aec26cd305c8'),
(57, 12, 'directions_7_direction', 'Repeat until dish is full. Pour remaining enchilada sauce all over, making sure end of tortilla wraps are moist. Sprinkle remaining cheese on top.'),
(58, 12, '_directions_7_direction', 'field_5aec26cd305c8'),
(59, 12, 'directions_8_direction', 'Bake for 20 minutes covered with aluminum foil. Remove foil, and cook for 3 minutes more until cheese is fully melted. I choose to broil for a few minutes.'),
(60, 12, '_directions_8_direction', 'field_5aec26cd305c8'),
(61, 12, 'directions_9_direction', 'Let sit for 10 minutes, and spread the creamy cilantro lime sauce on top. Enjoy!!'),
