(2376, 533, '_directions_2_direction', 'field_5aec26cd305c8'),
(2377, 533, 'directions_3_direction', 'Cook burger patties on the stove.  Once 1 side is done, flip and add the american cheese. Cook until cheese is melted.'),
(2378, 533, '_directions_3_direction', 'field_5aec26cd305c8'),
(2379, 533, 'directions_4_direction', 'While burgers cook, spread ranch on the bottom half of the burger buns, and a very hefty amount of BBQ sauce on the top bun. '),
(2380, 533, '_directions_4_direction', 'field_5aec26cd305c8'),
(2381, 533, 'directions_5_direction', 'Place cooked burgers on the ranch side of the bun, stack about 3 onion rings per burger on top of the patty. Layer bacon on top of onion rings, and drizzle more BBQ sauce on top. Place top bun on, and ENJOY!'),
