(2706, 554, 'ingredients_0_ingredient', '1/2 cup coconut oil (the solidifed part only)'),
(2707, 554, '_ingredients_0_ingredient', 'field_5ae6ab14df826'),
(2708, 554, 'ingredients_1_ingredient', '1 and 1/4  cup brown sugar'),
(2709, 554, '_ingredients_1_ingredient', 'field_5ae6ab14df826'),
(2710, 554, 'ingredients_2_ingredient', '1 tsp vanilla extract'),
(2711, 554, '_ingredients_2_ingredient', 'field_5ae6ab14df826'),
(2712, 554, 'ingredients_3_ingredient', '1/4 cup canned Thai coconut milk'),
(2713, 554, '_ingredients_3_ingredient', 'field_5ae6ab14df826'),
(2714, 554, 'ingredients_4_ingredient', '1/4 cup unsweetened applesauce'),
(2715, 554, '_ingredients_4_ingredient', 'field_5ae6ab14df826'),
(2716, 554, 'ingredients_5_ingredient', '1/2 tsp salt'),
(2717, 554, '_ingredients_5_ingredient', 'field_5ae6ab14df826'),
(2718, 554, 'ingredients_6_ingredient', '2  and 1/4 cup all purpose flour'),
(2719, 554, '_ingredients_6_ingredient', 'field_5ae6ab14df826'),
(2720, 554, 'ingredients_7_ingredient', '1 tsp baking soda'),
(2721, 554, '_ingredients_7_ingredient', 'field_5ae6ab14df826'),
(2722, 554, 'ingredients_8_ingredient', '1 and 1/2-3/4 cups semi sweet vegan chocolate chips'),
(2723, 554, '_ingredients_8_ingredient', 'field_5ae6ab14df826'),
(2724, 554, 'ingredients', '9'),
(2725, 554, '_ingredients', 'field_5ae6a9cd8ce57'),
(2726, 554, 'directions_0_direction', 'In a large mixing bowl, whisk the solidified coconut oil, brown sugar, and vanilla extract until it is well combined.'),
(2727, 554, '_directions_0_direction', 'field_5aec26cd305c8'),
(2728, 554, 'directions_1_direction', 'Line a baking sheet with parchment paper, and preheat oven to 375 degrees F.'),
(2729, 554, '_directions_1_direction', 'field_5aec26cd305c8'),
(2730, 554, 'directions_2_direction', 'In the mixing bowl with the above mixture, add in the coconut milk and applesauce. Whisk again until well combined.'),
(2731, 554, '_directions_2_direction', 'field_5aec26cd305c8'),
(2732, 554, 'directions_3_direction', 'In a smaller bowl, combine the dry ingredients: flour, baking soda, and salt. Mix well.'),
(2733, 554, '_directions_3_direction', 'field_5aec26cd305c8'),
(2734, 554, 'directions_4_direction', 'Slowly add the dry ingredient mixture to the larger mixing bowl with the wet mixture. Use a wooden spoon to combine everything and it becomes one big ball of dough- will get thick!'),
(2735, 554, '_directions_4_direction', 'field_5aec26cd305c8'),
(2736, 554, 'directions_5_direction', 'Add in your chocolate chips. Keep folding them in until they are well mixed.'),
(2737, 554, '_directions_5_direction', 'field_5aec26cd305c8'),
(2738, 554, 'directions_6_direction', 'Scoop cookie dough balls about 2 tbs sized (can be smaller) onto the parchment paper lined cookie sheet. Leave an inch between each cookie. There will be enough dough for about 3 different batches.'),
(2739, 554, '_directions_6_direction', 'field_5aec26cd305c8'),
(2740, 554, 'directions_7_direction', 'Bake cookies for about 8-9 minutes, or until they are golden brown underneath and on the edges. Remove from oven and leave on the cookie sheet for about 5 minutes, and then remove to cool on a separate dish or cooling rack. Enjoy!!'),
(2741, 554, '_directions_7_direction', 'field_5aec26cd305c8'),
(2742, 554, 'directions', '8'),
