(2755, 556, '_ingredients_4_ingredient', 'field_5ae6ab14df826'),
(2756, 556, 'ingredients_5_ingredient', '<a target=\"_blank\" href=\"https://wayfarefoods.com/shop/sour-cream/\">Wayfare Sour Cream</a> or <a target=\"_blank\" href=\"https://followyourheart.com/products/sour-cream-3//\">Follow Your Heart Sour Cream</a>'),
