(2760, 556, 'directions', '6'),
(2761, 556, '_directions', 'field_5aec26bb305c7'),
(2762, 556, 'ingredients_6_ingredient', 'Vegan Nacho Cheese (<a target=\"_blank\" href=\"https://wayfarefoods.com/product/nacho-cheddar/\">Wayfare Nacho Cheese</a> is my favorite or my recipe <a  target=\"_blank\" href=\"https://www.veganandcheese.com/easy-vegan-recipes/vegan-queso/\">here</a>) or your favorite Vegan cheese shreds'),
(2763, 556, '_ingredients_6_ingredient', 'field_5ae6ab14df826'),
(2764, 556, 'ingredients_7_ingredient', 'Optional: Brown or White Rice, cooked. I mix in some salsa to make it more \"mexican style\"'),
(2765, 556, '_ingredients_7_ingredient', 'field_5ae6ab14df826'),
(2766, 556, 'directions_0_direction', 'Prepare vegan meat substitute according to package directions. My go to is Cena Vegan Pollo Asada (above).'),
(2767, 556, '_directions_0_direction', 'field_5aec26cd305c8'),
(2768, 556, 'ingredients_8_ingredient', 'Large tortillas (burrito size)'),
(2769, 556, '_ingredients_8_ingredient', 'field_5ae6ab14df826'),
(2770, 556, 'ingredients_9_ingredient', 'Nonstick cooking spray'),
(2771, 556, '_ingredients_9_ingredient', 'field_5ae6ab14df826'),
(2772, 556, 'directions_1_direction', 'Warm up refried beans in a small saucepan on the stove. Heat queso up if using.'),
(2773, 556, '_directions_1_direction', 'field_5aec26cd305c8'),
(2774, 556, 'directions_2_direction', 'Assemble burritos before grilling: Spread refried beans in the middle of the tortilla. Scoop a big spoonful of rice on top of beans, followed by your vegan meat protein. '),
(2775, 556, '_directions_2_direction', 'field_5aec26cd305c8'),
(2776, 556, 'ingredients_10_ingredient', 'Optional: <a target=\"_blank\" href=\"https://www.veganandcheese.com/easy-vegan-recipes/creamy-cilantro-lime-sauce/\">Creamy Cilantro Lime Dressing</a>'),
(2777, 556, '_ingredients_10_ingredient', 'field_5ae6ab14df826'),
(2778, 556, 'directions_3_direction', 'Scoop and spread guacamole next, followed by sour cream. Next add the shredded lettuce, followed by topping with your nacho cheese or cheese shreds!'),
(2779, 556, '_directions_3_direction', 'field_5aec26cd305c8'),
(2780, 556, 'directions_4_direction', 'Fold the top of tortilla in, and the bottom, and the roll up tightly. Warm up a frying pan on the stove and cover in nonstick cooking spray. Place burrito in, when it turns golden/light brown, flip and grill the other side until light brown. '),
(2781, 556, '_directions_4_direction', 'field_5aec26cd305c8'),
(2782, 556, 'directions_5_direction', 'Optional: top with cilantro lime dressing, and enjoy!'),
(2783, 556, '_directions_5_direction', 'field_5aec26cd305c8'),
