(4253, 764, 'ingredients_0_ingredient', '5 lbs of golden yukon potatoes, cut in 2 inch pieces'),
(4254, 764, '_ingredients_0_ingredient', 'field_5ae6ab14df826'),
(4255, 764, 'ingredients_1_ingredient', '1 cup of vegan chicken broth or vegetable broth'),
(4256, 764, '_ingredients_1_ingredient', 'field_5ae6ab14df826'),
(4257, 764, 'ingredients_2_ingredient', '4 cloves of garlic, crushed'),
(4258, 764, '_ingredients_2_ingredient', 'field_5ae6ab14df826'),
(4259, 764, 'ingredients_3_ingredient', '1/2 cup of vegan butter diced (such as Earth Balance or Miyoko\'s)'),
(4260, 764, '_ingredients_3_ingredient', 'field_5ae6ab14df826'),
(4261, 764, 'ingredients_4_ingredient', '1/2 cup to 3/4 cup of vegan cream cheese (depending on how rich you like your potatoes)'),
(4262, 764, '_ingredients_4_ingredient', 'field_5ae6ab14df826'),
(4263, 764, 'ingredients_5_ingredient', '1/2 cup vegan milk (I prefer Cashew Milk)'),
(4264, 764, '_ingredients_5_ingredient', 'field_5ae6ab14df826'),
(4265, 764, 'ingredients_6_ingredient', 'Pepper to taste'),
(4266, 764, '_ingredients_6_ingredient', 'field_5ae6ab14df826'),
(4267, 764, 'ingredients_7_ingredient', 'Salt to taste'),
(4268, 764, '_ingredients_7_ingredient', 'field_5ae6ab14df826'),
(4269, 764, 'ingredients', '8'),
(4270, 764, '_ingredients', 'field_5ae6a9cd8ce57'),
(4271, 764, 'directions_0_direction', 'Place the potatoes, broth, garlic, and butter into your crockpot. Season with salt, and mix well.'),
(4272, 764, '_directions_0_direction', 'field_5aec26cd305c8'),
(4273, 764, 'directions_1_direction', 'Cook on high approximately 4 hours, or 7 if cooking on low.'),
(4274, 764, '_directions_1_direction', 'field_5aec26cd305c8'),
(4275, 764, 'directions_2_direction', 'When potatoes are very soft and tender, mash potatoes with a potato masher and stir until very creamy. You can use an electric hand mixer as well. Mix in the vegan cream cheese and milk until creamy. Add more cream cheese or milk as needed to get the consistency you like.'),
(4276, 764, '_directions_2_direction', 'field_5aec26cd305c8'),
(4277, 764, 'directions_3_direction', 'Season with pepper to taste, and keep on warm until ready to serve! Goes great on its own or with vegan gravy!'),
(4278, 764, '_directions_3_direction', 'field_5aec26cd305c8'),
