(4289, 766, 'ingredients_0_ingredient', '1 store bought pizza dough (most are vegan)'),
(4290, 766, '_ingredients_0_ingredient', 'field_5ae6ab14df826'),
(4291, 766, 'ingredients_1_ingredient', '1 8oz block of vegan mozzarella (I recommend <a target=\"_blank\" href=\"https://miyokos.com/products/fresh-vegan-mozzarella-cheese\">Miyoko\'s Mozzarella</a> or <a target=\"_blank\" href=\"https://followyourheart.com/products/mozzarella-3/\">Follow Your Heart Mozzarella block</a>'),
(4292, 766, '_ingredients_1_ingredient', 'field_5ae6ab14df826'),
(4293, 766, 'ingredients_2_ingredient', 'Egg Wash (I make 1 vegan egg using Egg Replacer Powder and add a little extra water)'),
(4294, 766, '_ingredients_2_ingredient', 'field_5ae6ab14df826'),
(4295, 766, 'ingredients_3_ingredient', '1/4 cup melted vegan butter'),
(4296, 766, '_ingredients_3_ingredient', 'field_5ae6ab14df826'),
(4297, 766, 'ingredients_4_ingredient', '1/2 cup grated vegan Parmesan (I like <a target=\"_blank\" href=\"https://violifefoods.com/us/product/just-like-parmesan-wedge/\">Violife Foods Parmesan Block</a>)'),
(4298, 766, '_ingredients_4_ingredient', 'field_5ae6ab14df826'),
(4299, 766, 'ingredients_5_ingredient', '1 tbsp of dried basil'),
(4300, 766, '_ingredients_5_ingredient', 'field_5ae6ab14df826'),
(4301, 766, 'ingredients_6_ingredient', '1 tbsp of dried rosemary'),
