(4375, 769, 'ingredients_0_ingredient', '1 box of 12 oz jumbo pasta shells'),
(4376, 769, '_ingredients_0_ingredient', 'field_5ae6ab14df826'),
(4377, 769, 'ingredients_1_ingredient', '1/2 cup finely chopped onion'),
(4378, 769, '_ingredients_1_ingredient', 'field_5ae6ab14df826'),
(4379, 769, 'ingredients_2_ingredient', '1 package (approx 1 pound) of vegan italian sausage (Suggestion: <a target=\"_blank\" href=\"http://beyondmeat.com/products/view/beyond-sausage-hot\">Beyond Sausage Hot Italian</a>)'),
(4380, 769, '_ingredients_2_ingredient', 'field_5ae6ab14df826'),
