(5358, 841, 'ingredients_0_ingredient', '1/2 a bag of <a target=\"_blank\" href=\"http://www.cenavegan.com/\">Cena Vegan</a>  Pollo Asada. This is about 2 cups. Beyond Meat Chicken would also work, or any vegan chicken substitute.'),
(5359, 841, '_ingredients_0_ingredient', 'field_5ae6ab14df826'),
(5360, 841, 'ingredients_1_ingredient', '1 can of vegan cream of chicken soup- my recipe can be found <a target=\"_blank\" href=\"https://www.veganandcheese.com/easy-vegan-recipes/vegan-cream-of-chicken-soup/\">here</a>: (I added a few spoonfuls of vegan sour cream to thicken it up)'),
(5361, 841, '_ingredients_1_ingredient', 'field_5ae6ab14df826'),
(5362, 841, 'ingredients_2_ingredient', '1/2 a 16oz package of frozen mixed vegetables (peas, green beans, carrots, corn), thawed'),
(5363, 841, '_ingredients_2_ingredient', 'field_5ae6ab14df826'),
(5364, 841, 'ingredients_3_ingredient', '2 packages/tubes of refrigerated biscuit dough'),
