(5368, 841, 'ingredients_5_ingredient', '1/2 cup of vegan shredded cheddar cheese'),
(5369, 841, '_ingredients_5_ingredient', 'field_5ae6ab14df826'),
(5370, 841, 'directions_0_direction', 'Preheat oven to 350 degrees F.'),
(5371, 841, '_directions_0_direction', 'field_5aec26cd305c8'),
(5372, 841, 'directions_1_direction', 'In 1 large bowl, mix in the vegan chicken, thawed vegetables, vegan cream of chicken soup.'),
(5373, 841, '_directions_1_direction', 'field_5aec26cd305c8'),
(5374, 841, 'directions_2_direction', 'Spray a muffin pan with non stick spray.'),
(5375, 841, '_directions_2_direction', 'field_5aec26cd305c8'),
(5376, 841, 'directions_3_direction', 'Sprinkle flour on a cutting board, and unroll the biscuit dough. Flatten biscuit dough slightly, and press into the muffin tin cups one by one, pressing against the sides and bottom of the cups.'),
(5377, 841, '_directions_3_direction', 'field_5aec26cd305c8'),
(5378, 841, 'directions_4_direction', 'Pour the chicken mixture into the muffin cups, about 1/3 cup at at time. Make sure filling is level at the top.'),
(5379, 841, '_directions_4_direction', 'field_5aec26cd305c8'),
(5380, 841, 'directions_5_direction', 'Optional: Sprinkle vegan cheddar cheese on top. Place muffin tins in the oven, and bake for about 15 minutes or until the biscuits are golden brown. Let cool slightly- and enjoy!'),
(5381, 841, '_directions_5_direction', 'field_5aec26cd305c8'),
