(5653, 863, '_directions_0_direction', 'field_5aec26cd305c8'),
(5654, 863, 'directions_1_direction', 'Heat a skillet with olive oil, and saute the onion until translucent. Next, add the garlic and bell pepper to the skillet and saute until all are golden.'),
(5655, 863, '_directions_1_direction', 'field_5aec26cd305c8'),
