(5871, 884, 'ingredients_0_ingredient', '1/2 cup of cashews'),
(5872, 884, '_ingredients_0_ingredient', 'field_5ae6ab14df826'),
(5873, 884, 'ingredients_1_ingredient', '3/4 cup of almond milk (can also use cashew milk or vegan milk of choice)'),
